import React from "react";
import { HCP_list } from "../components/HCPPage/HCP_list.js";
import { Link } from "react-router-dom";
import '/Users/alexselyukov/Documents/napp/my-app/src/CSS/main.css';

export default function HCP () {

    return (
      <main className="main">
        <div className="descr-title">
            <div className="descr-title-container container">
                <h1 className="descr-title-text">Врачи</h1>
            </div>
        </div>

        <div className="hcp-container container">
            <ul className="hcp-list list-reset flex">
                    {HCP_list.map((hcp) => 
                    <li key={hcp.id} className="hcp-card-bckg flex">
                        <Link to={hcp.path}>
                            {/* <img src={hcp.image} className=".hcp-img"/> */}

                            <div className="hcp-card-info flex" style = {{backgroundImage:`url(${hcp.image})`}}>
                                <a className="hcp-card-name" href={hcp.path}>
                                    {hcp.fullName}
                                </a>
                                <div className="hcp-card-dscr flex">
                                    <a className="" href={hcp.path}>
                                        {hcp.specialties}
                                    </a>
                                    <a className="hcp-more flex" href={hcp.path}>
                                        ПОДРОБНЕЕ
                                    </a>
                                </div>
                            </div>
                        </Link>

                    </li>
                    )}
                </ul>
        </div>
      </main>
    )
  }
