import React from 'react';
import { Link, useLocation, useState} from "react-router-dom";


export default function Page_title(props) {
    const title = props;
    const location = useLocation();
    const [action, setAction] = useState(false);
    const path = location.pathname;
    const massive = path.split('/');
    delete massive[massive.length - 1];
    let newPath = massive.join('/');
    if (setAction === true) {
        delete massive[massive.length - 1];
        newPath = massive.join('/');
    }

    return (
        <div className="descr-title">
            <Link to={newPath} className="descr-title-container container flex" onClick={setAction === true}>
                <div className="back-group flex">
                    <span className="back-line"></span>
                    <span className="back">Назад</span>
                </div>
                <h1 className="descr-title-text">{title.getTitle}</h1>
            </Link>
        </div>
    )
}
