import React from 'react';
import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom';


export default function Page_title(props) {
    const title = props;
    const path = location.pathname;
    console.log(path)
    // const newPath  = path.split('/')[0];

    return (
        <div className="descr-title">
            <Link to={path} className="descr-title-container container flex">
                <div className="back-group flex">
                    <span className="back-line"></span>
                    <a className="back">Назад</a>
                </div>
                <h1 className="descr-title-text">{title.getTitle}</h1>
            </Link>
        </div>
    )
}
